{
    "environmentName": "vrops_deployment",
    "infrastructure": {
        "properties": {
            "dataCenterVmid": "{{datacenter_vmid}}",
            "regionName": "default",
            "zoneName": "default",
            "vCenterName": "{{vc_mgmt_hostname_short}}",
            "vCenterHost": "{{vc_mgmt_hostname_long}}",
            "vcUsername": "{{vcenter_password_username}}",
            "vcPassword": "locker:password:{{vcenter_password_vmid}}:{{vcenter_password_alias}}",
            "acceptEULA": "true",
            "enableTelemetry": "false",
            "defaultPassword": "locker:password:{{simple_password_vmid}}:{{simple_password_alias}}",
            "certificate": "locker:certificate:{{vrops_certificate_vmid}}:{{vrops_certificate_alias}}",
            "cluster": "{{vc_datacenter_name}}#{{vc_mgmt_clustername}}",
            "storage": "{{vsan_dsname}}",
            "folderName": "",
            "resourcePool": "",
            "diskMode": "thin",
            "network": "{{avn1_name}}",
            "masterVidmEnabled": "false",
            "dns": "{{dns_primaryns}},{{dns_secondaryns}}",
            "domain": "{{dns_subdomain}}",
            "gateway": "{{avn1_gateway}}",
            "netmask": "{{avn1_subnet_mask}}",
            "searchpath": "{{avn1_gateway}}",
            "timeSyncMode": "ntp",
            "ntp": "{{ntps_primary}},{{ntps_secondary}}",
            "isDhcp": "false",
            "vcfProperties": "{\"vcfEnabled\":true,\"sddcManagerDetails\":[{\"sddcManagerHostName\":\"{{sddcm_host_long}}\",\"sddcManagerName\":\"default\",\"sddcManagerVmid\":\"default\"}]}"
        }
    },
    "products": [
        {
            "id": "vrops",
            "version": "8.6.2",
            "patchHistory": null,
            "snapshotHistory": null,
            "logHistory": null,
            "clusterVIP": {
                "clusterVips": [
                    {
                        "type": "vrops-lb",
                        "properties": {
                            "hostName": "{{vrops_vip}}",
                            "lockerCertificate": "locker:certificate:{{vrops_certificate_vmid}}:{{vrops_certificate_alias}}"
                        }
                    }
                ]
            },
            "nodes": [
                {
                    "type": "master",
                    "properties": {
                        "vmName": "{{vrops_fqdn1}}",
                        "hostName": "{{vrops_fqdn1}}",
                        "ip": "{{vrops_ip1}}"
                    }
                },
                {
                    "type": "replica",
                    "properties": {
                        "vmName": "{{vrops_fqdn2}}",
                        "hostName": "{{vrops_fqdn2}}",
                        "ip": "{{vrops_ip2}}"
                    }
                }
            ],
            "collectorGroups": null,
            "properties": {
                "certificate": "locker:certificate:{{vrops_certificate_vmid}}:{{vrops_certificate_alias}}",
                "productPassword": "locker:password:{{simple_password_vmid}}:{{simple_password_alias}}",
                "licenseRef": "locker:license{{locker_license_vmid}}:{{locker_license_alias}}",
                "disableTls": "TLSv1,TLSv1.1,TLSv1.2",
                "fipsMode": "false",
                "timeSyncMode": "ntp",
                "masterVidmEnabled": true,
                "ntp": "{{ntps_primary}},{{ntps_secondary}}",
                "affinityRule": true,
                "configureAffinitySeparateAll": "true",
                "deployOption": "small",
                "isCaEnabled": "false",
                "contentLibraryItemId": "",
                "contentLibraryItemId:proxy": ""
            }
        }
    ],
    "metaData": {
        "isCloudProxyEnvironment": "false"
    }
}
